import React, { Component } from 'react';

import './RegisterPage.scss';
import { NavLink } from "react-router-dom"
import axios from 'axios'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./"

class RegisterPage extends Component {
    state = {
        password: '',
        email: '',
        firstname: '',
        lastname: '',
        forget_password: ''
    }

    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmitRegister = (ev) => { 
        ev.preventDefault()
         const firstname=ev.target.firstname.value
         const lastname=ev.target.lastname.value
        const email = ev.target.email.value
        const password = ev.target.password.value
        if(  firstname!=="" || lastname!=="" || email!==""|| password!=="") {

            axios.post("http://localhost:3000/registration/registration", firstname,lastname,email,password)
                .then(resp => {
                    if (resp.data.error === "User already exists") {
                        toast.error("Ce compte existe déja");
                    } else {
                        toast.success("Votre compte a bien été enregistré");
                        window.location.assign('/login')
                    }
                })
                .catch(error=> {
                    return toast.error("Erreur lors de d'enregistrement veuillez essayer ultérieurement ")
                  })

            // toast.success("Votre compte a bien été enregistré");
          }

          else{
           toast.error("Formulaire non valide")
           return false
          }


    }

    render() {
        return (
            <>
                <div>
                    <div className="bg-container">
                        <div className="container-register">
                            <img src="" alt="logo spiky" className="logo-spiky" />
                            <form className="form-signup" onSubmit={this.onSubmitRegister}>
                                <div className="field-signup">
                                    <label className="label-signup" htmlFor="firstname">Prénom</label>
                                    <input className="signup-input" type="firstname" name="firstname" value={this.state.firstname} onChange={this.changeHandler} />
                                </div>
                                <div className="field-signup">
                                    <label className="label-signup" htmlFor="lastname">Nom</label>
                                    <input className="signup-input" type="lastname" name="lastname" value={this.state.lastname} onChange={this.changeHandler} />
                                </div>
                                <div className="field-signup">
                                    <label className="label-signup" htmlFor="email">Email</label>
                                    <input className="signup-input" type="email" name="email" value={this.state.email} onChange={this.changeHandler} />

                                </div>
                                <div className="field-signup">
                                    <label className="label-signup" htmlFor="password">Mot de passe</label>
                                    <input className="signup-input" type="password" name="password" value={this.state.password} onChange={this.changeHandler} />

                                </div>
                                <div className="field-signup">
                                    <label className="label-signup" htmlFor="comfirm_password">Confirmer son mot de passe</label>
                                    <input className="signup-input" type="password" name="comfirm_password" value={this.state.confim_password} onChange={this.changeHandler} />

                                </div>

                                <span className="checkbox-connection"><input type="checkbox" />J'accepte les conditions et les termes</span>
                                <button className="btn-signup">S'INSCRIRE</button>
                                <div className="already-account">
                                    <h3 className="already-account-title">Vous avez déjà un compte? <NavLink to="/login" className="not-account-signup-link">Cliquez ici</NavLink></h3>
                                </div>

                            </form>
                        </div>

                    </div>
                    <ToastContainer autoClose={10000} />
                </div>
            </>
        )
    }

}
export default RegisterPage;
