import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import "./LoginPage.scss"
import axios from 'axios'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class LoginPage extends Component {
    state = {
        password: '',
        email: '',
        loggedIn: false

    }
    onSubmitLogin = e => {
        e.preventDefault()
        const email = e.target.email_login.value
        const password = e.target.password_login.value
        if (email === '' || password === '') {
            toast.error("veuillez  remplir les champs ");
        } else {
            axios.post('http://localhost:3000/login/login', {
                email: e.target.email_login.value,
                password: e.target.password_login.value,
            }).then(res => {
                localStorage.setItem('token', res.headers["x-access-token"])
                const token = localStorage.getItem('token')
                if (token) {
                    axios({
                        method: 'POST',
                        url: 'http://localhost:3000/login/protected',
                        headers: { 'Authorization': `Bearer ${token}` }
                    })
                        .then(res => {
                            if (res.data.message === 'Token OK') {
                                window.location.assign('/Conversations')
                            } else {
                                res.json({ success: false, message: 'Authentication failed, wrong password buddy' });
                                toast.warning("Problem has occurred please try again later");
                            }

                        })
                }
                else {
                    return toast.error("Un problème est survenu, veuillez réessayer plus tard");
                }
            })
                .catch(error => {
                    return toast.error("Les Données entrées sont  incorrectes")
                })

        }


    }


    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
        return (
            <div className="login-block">
                <div className="login-container-form">
                    <img src="" alt="logo spiky" className="logo-spiky" />
                    <form className="form-login" onSubmit={this.onSubmitLogin}>
                        <div className="field-login">
                            <label className="label-login" htmlFor="email">Email</label>
                            <input className="login-input" type="email" name="email" value={this.state.email} onChange={this.changeHandler} />
                            {/* <FontAwesomeIcon className="icon-login" icon={faEnvelope} color="#a4a7bc" size="lg"/> */}
                        </div>
                        <div className="field-login">
                            <label className="label-login" htmlFor="password">Mot de passe</label>
                            <input className="login-input" type="password" name="password" value={this.state.password} onChange={this.changeHandler} />
                            {/* // <FontAwesomeIcon className="icon-login" icon={faLock} color="#a4a7bc" size="lg"/> */}
                        </div>
                        <span className="checkbox-connection"><input type="checkbox" />Rester connecté</span>
                        <button className="btn-login" onClick={this.handleLogin} >SE CONNECTER</button>

                        <h5 className="forget-mdp-text">Mot de passe oublié? Cliquer ici</h5>

                    </form>
                    <div className="not-account">
                        <h3 className="not-account-title">Vous n'avez pas encore de compte ?</h3>
                        <button className="not-account-signup"><NavLink to="/register" className="not-account-signup-link">CRÉER SON COMPTE</NavLink></button>
                    </div>
                </div>

                <ToastContainer autoClose={10000} />

            </div>
        );
    }
}

export default LoginPage;
