import React, { Component } from 'react';

import { NavLink } from "react-router-dom"
import axios from 'axios'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./RegisterPage.css"

class RegisterPage extends Component {
    state = {
        password: '',
        email: '',
        firstname: '',
        lastname: '',

    }

    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmitRegister = (ev) => { 
        ev.preventDefault()
         const firstname=ev.target.firstname.value
         const lastname=ev.target.lastname.value
        const email = ev.target.email.value
        const password = ev.target.password.value

        if(  firstname!=="" || lastname!=="" || email!==""|| password!=="") {
       const objectdata={
         firstname:ev.target.firstname.value,
        lastname:ev.target.lastname.value,
      email : ev.target.email.value,
      password : ev.target.password.value
       }
            axios.post("http://localhost:3000/registration/registration",objectdata)
                .then(resp => {
                    if (resp.data.error === "User already exists") {
                        toast.error("Ce compte existe déja");
                    } else {

                        window.location.assign('/login')
                        toast.success("Votre compte a bien été enregistré");
                    }
                })
                .catch(error=> {
                    return toast.error("Erreur lors de d'enregistrement veuillez essayer ultérieurement ")
                  })


          }

          else{
           toast.error("Formulaire non valide")
           return false
          }


    }

    render() {
        return (
         <>


                <div className="register-box">
                <h2> S'enregistrer</h2>
                <form onSubmit={this.onSubmitRegister}>
                    <div className="user-box">
                        <input  type="text" name="firstname" value={this.state.firstname} onChange={this.changeHandler} required="" />
                        <label>Nom et Prénom</label>
                    </div>
                    <div className="user-box">
                        <input type="email" name="email" value={this.state.email} onChange={this.changeHandler} required="" />
                        <label>Email</label>
                    </div>
                    <div className="user-box">
                        <input type="password" name="password" value={this.state.password} onChange={this.changeHandler} required="" />
                        <label>Mot de passe</label>
                    </div>
                    <a href="#">
                        <button type="submit" onSubmit={this.onSubmitLogin}>Submit</button>
                        <span></span>
                        <span></span>
                        <span></span>
                        <span></span>

                    </a>
                    <h3 className="already-account-title">Vous avez déjà un compte? <NavLink to="/login" className="not-account-signup-link">Cliquez ici</NavLink></h3>
                </form>
                <ToastContainer autoClose={10000} />
            </div>
            </>
        )
    }

}
export default RegisterPage;
